parameters.jsCode:
// Parse backup script output for summary statistics
const scriptOutput = $input.item.json.stderr || '';
const scriptStdout = $input.item.json.stdout || '';
const exitCode = $input.item.json.exitCode;

// Extract key information using regex
const durationMatch = scriptOutput.match(/Duration: (\d+) seconds/);
const sizeMatch = scriptOutput.match(/Total backup size: (.+)/);
const dateMatch = scriptOutput.match(/Date: ([\d-]+)/);

const summary = {
  exitCode: exitCode,
  success: exitCode === 0,
  status: exitCode === 0 ? 'SUCCESS' : 'FAILURE',
  duration: durationMatch ? durationMatch[1] + ' seconds' : 'Unknown',
  backupSize: sizeMatch ? sizeMatch[1] : 'Unknown',
  backupDate: dateMatch ? dateMatch[1] : new Date().toISOString().split('T')[0],
  fullOutput: scriptOutput,
  stdout: scriptStdout,
  timestamp: new Date().toISOString(),
  uptimeKumaStatus: exitCode === 0 ? 'up' : 'down',
  uptimeKumaMsg: exitCode === 0 ? 'Backup completed successfully' : `Backup FAILED - Exit code: ${exitCode}`
};

return { json: summary };

